import React from 'react';
import { useAppContext } from '../context/AppContext';
import LanguageSelector from './LanguageSelector';
import FarmerDashboard from './FarmerDashboard';
import BuyerDashboard from './BuyerDashboard';

const KrishiConnect = () => {
  const { currentPage, userType } = useAppContext();

  if (currentPage === 'language') return <LanguageSelector />;
  if (currentPage === 'userType' || currentPage === 'farmerDashboard') {
    if (userType === 'farmer' || currentPage === 'farmerDashboard') return <FarmerDashboard />;
    return <BuyerDashboard />;
  }
  if (currentPage === 'buyerDashboard') return <BuyerDashboard />;

  return <LanguageSelector />;
};

export default KrishiConnect;
