// Add UserType buttons directly after language selection
{selectedLanguage && (
  <div className="mt-6 space-y-3">
    <button
      onClick={() => {
        setUserType('farmer');
        setCurrentPage('farmerDashboard');
      }}
      className="w-full py-4 bg-gradient-to-r from-green-600 to-emerald-600 text-white rounded-xl font-bold"
    >
      {t('yesFarmer')}
    </button>
    <button
      onClick={() => {
        setUserType('buyer');
        setCurrentPage('buyerDashboard');
      }}
      className="w-full py-4 bg-white border-2 border-green-600 text-green-700 rounded-xl font-bold"
    >
      {t('buyer')}
    </button>
  </div>
)}
